#include <openssl/aes.h>

namespace fs = std::filesystem;

void encryptFile(const std::string& password, const std::string& filePath)
{
    // Open the input file for reading
    std::ifstream inFile(filePath, std::ios::binary);
    if (!inFile)
    {
        std::cerr << "Error: unable to open input file " << filePath << std::endl;
        return;
    }

    // Create the output file and write the file header
    std::ofstream outFile(filePath + ".enc", std::ios::binary);
    if (!outFile)
    {
        std::cerr << "Error: unable to create output file " << filePath + ".enc" << std::endl;
        inFile.close();
        return;
    }
    outFile.write("ENCRYPTED", 9);

    // Generate the AES key and initialization vector from the password
    unsigned char key[AES_BLOCK_SIZE], iv[AES_BLOCK_SIZE];
    const char* passwordCStr = password.c_str();
    AES_set_encrypt_key(reinterpret_cast<const unsigned char*>(passwordCStr), 256, key);
    std::memcpy(iv, passwordCStr + strlen(passwordCStr) - AES_BLOCK_SIZE, AES_BLOCK_SIZE);

    // Encrypt the file data in chunks of AES_BLOCK_SIZE bytes
    char inBuf[AES_BLOCK_SIZE], outBuf[AES_BLOCK_SIZE];
    while (inFile.read(inBuf, AES_BLOCK_SIZE))
    {
        AES_cbc_encrypt(reinterpret_cast<const unsigned char*>(inBuf), reinterpret_cast<unsigned char*>(outBuf), AES_BLOCK_SIZE, key, iv, AES_ENCRYPT);
        outFile.write(outBuf, AES_BLOCK_SIZE);
    }

    // Encrypt any remaining bytes in the input file
    if (inFile.gcount() > 0)
    {
        std::memset(inBuf + inFile.gcount(), AES_BLOCK_SIZE - inFile.gcount(), AES_BLOCK_SIZE - inFile.gcount());
        AES_cbc_encrypt(reinterpret_cast<const unsigned char*>(inBuf), reinterpret_cast<unsigned char*>(outBuf), AES_BLOCK_SIZE, key, iv, AES_ENCRYPT);
        outFile.write(outBuf, AES_BLOCK_SIZE);
    }

    // Close the input and output files
    inFile.close();
    outFile.close();

    // Delete the original file
    std::remove(filePath.c_str());
}

