def evaluate_expression(expression, max_operations):
    # Check if expression is valid
    valid_chars = set('0123456789+-×÷')
    if not all(char in valid_chars for char in expression):
        return 'Error: Invalid expression'
    # Define order of operations
    operations = {'×': 2, '÷': 2, '+': 1, '-': 1}
    # Split expression into tokens
    tokens = []
    current_token = ''
    for char in expression:
        if char in operations or char == '(' or char == ')':
            if current_token:
                tokens.append(current_token)
                current_token = ''
            tokens.append(char)
        elif char.isdigit() or char == '.':
            current_token += char
        else:
            return 'Error: Invalid character'
    if current_token:
        tokens.append(current_token)
    # Evaluate expression
    stack = []
    operations_used = 0
    for token in tokens:
        if token.isdigit():
            stack.append(float(token))
        elif token in operations:
            if operations_used >= max_operations:
                return 'Error: Maximum number of allowed operations exceeded'
            operations_used += 1
            operand2 = stack.pop()
            operand1 = stack.pop()
            if token == '+':
                result = operand1 + operand2
            elif token == '-':
                result = operand1 - operand2
            elif token == '×':
                result = operand1 * operand2
            elif token == '÷':
                if operand2 == 0:
                    return 'Error: Division by zero'
                result = operand1 / operand2
            stack.append(result)
    if len(stack) != 1:
        return 'Error: Invalid expression'
    answer = stack[0]
    return f'{expression} = {answer}'
