// Generate a unique Message-ID for an email message
func generateMessageID() string {
    hostname, _ := os.Hostname()
    timestamp := time.Now().UnixNano()
    random := rand.Int63()
    return fmt.Sprintf("<%v.%v.%v@%v>", timestamp, random, os.Getpid(), hostname)
}

// Example usage in email sending function
