```
DELIMITER //
CREATE FUNCTION calculate_avg_age(condition_value INT) RETURNS DECIMAL(10,2)
BEGIN
    DECLARE total_age DECIMAL(10,2);
    DECLARE total_users INT;
    DECLARE avg_age DECIMAL(10,2);

    SELECT SUM(YEAR(CURDATE()) - birth_year) INTO total_age, COUNT(*) INTO total_users
    FROM users
    WHERE condition = condition_value;

    SET avg_age = total_age / total_users;

    RETURN avg_age;
END //
DELIMITER ;
